import React from "react";
import { Image, StyleSheet, Dimensions, Text, View } from 'react-native';
import logo from '../../../assets/logo.png';
import Texto from "../../components/Texto";

const width = Dimensions.get('screen').width;

export default function Cesta() {

    return <>

    <Topo />

    <View style={estilos.cesta}>
        <Texto style={estilos.nomeCesta}>Cesta de Verduras</Texto>

        <View style={estilos.fazenda}>
            <Image source={logo} style={estilos.imagemFazenda} />
            <Texto style={estilos.nomeFazenda}>Cria Local</Texto>
        </View>

        <Texto style={estilos.descricao}>Uma cesta de produtos selecionados cuidadosamente de produtores locais direto para a sua cozinha!</Texto>
        <Texto style={estilos.preco}>R$ 40,00</Texto>
    </View>

    </> 
}

const estilos = StyleSheet.create ({

    cesta: {
        paddingVertical: 8,
        paddingHorizontal: 16,
    },
    nomeCesta: {
        color: "#464646",
        fontSize: 26,
        lineHeight:42,
        fontWeight: "bold",
    },
    fazenda: {
        flexDirection: "row",
        paddingVertical: 12,
        marginLeft: 12,
    },
    imagemFazenda: {
        width: 32,
        height: 32,
    },
    nomeFazenda: {
        fontSize: 16,
        lineHeight: 26,
    },
    descricao: {
        color: "#A3A3A3",
        fontSize: 16,
        lineHeight: 26
    },
    preco: {
        color: "#2a9f85",
        fontWeight: "bold",
        fontSize: 26,
        lineHeight: 42,
        marginTop: 8,
    }
});
